// we have used event handling and hooks in this component


import React, {useState} from 'react'


export default function Textform(props) {
    const handleUpClick = ()=>{
        console.log("upper case was clicked" + text)
        let newTxt = text.toUpperCase();
        setText(newTxt)
    }
    const handleLoClick = ()=>{
        // console.log("upper case was clicked" + text)
        let newTxt = text.handleLoClick();
        setText(newTxt)
    }

    const handleOnChange = (event)=>{
        console.log("on change")
        setText(event.target.value);
    }
    const [text,setText] = useState("Enter the Text to convert it into uppercase");
    return (
        <div>
            <div className="mb-3">
                 <h1>{props.heading}</h1>
                <textarea className="form-control" value ={text} onChange={handleOnChange} id="exampleFormControlInput1" rows="6"></textarea>
            </div>
            <button className="btn btn-primary mx-2" onClick={handleUpClick}>Convert to UpperCase</button>
             <button className="btn btn-primary mx-2" onClick={handleLoClick}>Convert to Lower Case</button>
        </div>
    )
}
